#include <iostream>

// 逆Fibonacci計算器
int recursiveFibonacci(int n) {
    if (n <= 1) {
        return n;
    } else {
        return recursiveFibonacci(n - 1) + recursiveFibonacci(n - 2);
    }
}

// 進階Fibonacci計算器
int iterativeFibonacci(int n) {
    if (n <= 1) {
        return n;
    }

    int a = 0, b = 1;

    for (int i = 2; i <= n; i++) {
        int temp = a;
        a = b;
        b = temp + b;
    }

    return b;
}

int main() {
    int n;

    std::cout << "Enter the value of n: ";
    std::cin >> n;

    if (n < 0) {
        std::cerr << "Error: n must be a non-negative integer." << std::endl;
        return 1;
    }

    int result = n < 2 ? recursiveFibonacci(n) : iterativeFibonacci(n);

    std::cout << "第 " << n << " 個費波那契數是: " << result << std::endl;

    return 0;
}
```

